import React, { Component } from "react";
import CourseService from "../services/course.service";
import CreateQuestion from "./questions/createQuestion.component";
import CreateLesson from "./lesson/createLesson.component";
import NavAdmin from "./nav/navAdmin";
import ListLesson from "./lesson/listCourse.component";
import { useState } from "react";
import courseService from "../services/course.service";
import "./course.css";
export default class Course extends Component {
  constructor(props) {
    super(props);
    this.onChangeTitle = this.onChangeTitle.bind(this);
    this.onChangeDescription = this.onChangeDescription.bind(this);
    this.getACourse = this.getACourse.bind(this);
    this.updateCourse = this.updateCourse.bind(this);
    this.deleteCourse = this.deleteCourse.bind(this);
    this.state = {
      currentCourse: {
        id: null,
        title: "",
        description: "",
      },
      message: "",
    };
  }
  componentDidMount() {
    this.getACourse(this.props.match.params.id);
  }

  onChangeTitle(e) {
    const title = e.target.value;
    this.setState(function (prevState) {
      return {
        currentCourse: {
          ...prevState.currentCourse,
          title: title,
        },
      };
    });
  }

  onChangeDescription(e) {
    const description = e.target.value;

    this.setState((prevState) => ({
      currentCourse: {
        ...prevState.currentCourse,
        description: description,
      },
    }));
  }

  getACourse(id) {
    CourseService.getACourse(id)
      .then((response) => {
        this.setState({
          currentCourse: response.data.Course,
        });
        console.log(response.data);
      })
      .catch((e) => {
        console.log(e);
      });
  }

  updateCourse() {
    CourseService.updateCourse(
      this.state.currentCourse._id,
      this.state.currentCourse
    )
      .then((response) => {
        console.log(response.data);
        this.setState({
          message: "The tutorial was updated successfully!",
        });
      })
      .catch((e) => {
        console.log(e);
      });
  }
  //   this.state.currentCourse.id
  deleteCourse() {
    CourseService.deleteCourse(this.state.currentCourse._id)
      .then((response) => {
        console.log(response.data.Course);
        this.props.history.push("/admin/question");
      })
      .catch((e) => {
        console.log(e);
      });
  }

  render() {
    const { currentCourse } = this.state;

    return (
      <div>
        <NavAdmin />
        <div className="container pt-4">
          {currentCourse ? (
            <div className="container">

              <div id="course_menu" className="card p-4">
                <div className="card-title">
                  <h2>Course menu</h2>
                </div>
                <div className="card-body bg-danger">
                  <intput type="radio" value="Edit">Edit</intput>
                  <ul>
                    <li><a href="#edit_course">Edit</a></li>
                    <li>Create lesson</li>
                    <li>View all lessons</li>
                  </ul>
                </div>
              </div>

              <div id="edit_course" className="card p-4 d-none">
                <h4 className="form-title">Edit course</h4>
                <form>
                  <div className="form-group">
                    <label htmlFor="title" className="form-label">
                      Title
                    </label>
                    <input
                      type="text"
                      className="form-control"
                      id="title"
                      value={currentCourse.title}
                      onChange={this.onChangeTitle}
                    />
                  </div>
                  <div className="form-group">
                    <label htmlFor="description" className="form-label">
                      Description
                    </label>
                    <textarea
                      type="text"
                      className="form-control"
                      rows="5"
                      style={{ resize: "none" }}
                      id="description"
                      value={currentCourse.description}
                      onChange={this.onChangeDescription}
                    />
                  </div>
                </form>

                <div className="form-group d-flex justify-content-start">
                  <button
                    className="btn btn-outline-danger mt-4 w-25 me-4"
                    onClick={this.deleteCourse}
                  >
                    Delete
                  </button>
                  <button
                    type="submit"
                    className="btn btn-outline-primary mt-4 w-25"
                    onClick={this.updateCourse}
                  >
                    Update
                  </button>
                </div>
                <p>{this.state.message}</p>
              </div>

              <div id="create_lesson" className="card p-4">
                <CreateLesson id={this.props.match.params.id} />
              </div>

              <div id="view_lesson" className="card p-4">
                <ListLesson id={this.props.match.params.id} />
              </div>
            </div>
          ) : (
            <div>
              <br />
              <p>Please click on a Tutorial...</p>
            </div>
          )}
        </div>
      </div>
    );
  }
}
