import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import CommentService from "../../services/comment.service";
import relativeTime from "dayjs/plugin/relativeTime";
import dayjs from "dayjs";
dayjs.extend(relativeTime);
export default function CommentList() {
  const [comments, setComment] = useState([]);
  const [text, setText] = useState("");
  const [idUser, setIdUser] = useState(0);
  const { questionId } = useParams();

  useEffect(() => {
    getComments();
    getUser();
  }, []);

  function getUser() {
    const u = JSON.parse(localStorage.getItem("user"));
    const idU = u.id;
    setIdUser(idU);
    console.log(idU);
  }

  function getComments() {
    CommentService.getComments(questionId).then((comment) => {
      setComment(comment.data.Comment);
    });
  }
  function createComment() {
    var data = {
      text,
      aQuestion: questionId,
      author: idUser,
    };
    CommentService.createComment(data, questionId)
      .then((response) => {
        console.log(response.data);
      })
      .catch((e) => {
        console.log(e);
      });
  }
  function onChangeText(e) {
    setText(e.target.value);
  }

  return (
    <div className="container pt-4 pb-4 ">
      <div>
        {/* Button trigger modal */}
        <button
          type="button"
          className="btn btn-primary"
          data-bs-toggle="modal"
          data-bs-target="#add_comment"
        >
          Comment
        </button>
        {/* Modal */}
        <div
          className="modal fade"
          id="add_comment"
          tabIndex={-1}
          aria-labelledby="exampleModalLabel"
          aria-hidden="true"
        >
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="card">
                <div className="card-body pt-2 pb-2">
                  <h4 className="card-title">User: {idUser}</h4>
                  <label htmlFor="">
                    <h3>Add a comment</h3>
                  </label>
                  <textarea
                    className="form-control"
                    rows="6"
                    style={{ resize: "none" }}
                    onChange={onChangeText}
                    value={text}
                  />
                  <button
                    className="btn btn-success mt-2 mb-2"
                    onClick={createComment}
                  >
                    Send
                  </button>
                </div>
              </div>
              <div className="modal-footer">
                <button
                  type="button"
                  className="btn btn-secondary"
                  data-bs-dismiss="modal"
                >
                  Close
                </button>
                <button type="button" className="btn btn-primary">
                  Save changes
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="container pb-4 pt-4">
        <h4>Recent comments</h4>
        {comments.map((comment, index) => (
          <div className="card mt-2" key={index}>
            <div className="card-body">
              <h5 className="card-title">
                {comment.author.username}{" "}
                <small className="fw-normal">
                  {dayjs().to(dayjs(comment.createdAt))}
                </small>
              </h5>
              <p className="card-text">{comment.text}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

