import React from "react";
import { useState } from "react";
import { useParams } from "react-router-dom";
import QuestionService from "../../services/question.service";
import NavAdmin from "../nav/navAdmin";
export default function CreateQuestion({ id }) {
  const [aQuestion, setAQuestion] = useState("");
  const [description, setDescription] = useState("");
  const [answers, setAnswers] = useState([]);
  const [correct, setCorrect] = useState("");
  //const [numberQuestion, setNumberQuestion] = useState(1);
  const [success, setSuccess] = useState(false);
  // const [id] = useParams();

  function addAnswer() {
    setAnswers([...answers, ""]);
  }

  function saveQuestion() {
    var data = {
      aQuestion,
      description,
      answers: [...answers, correct],
      correct,
    };
    QuestionService.createQuestion(data, id)
      .then((response) => {
        setSuccess(true);
        console.log(response.data);
      })
      .catch((e) => {
        console.log(e);
      });
  }

  function onChangeAQuestion(e) {
    setAQuestion(e.target.value);
  }
  function onChangeDescription(e) {
    setDescription(e.target.value);
  }
  function onChangeCorrect(e) {
    setCorrect(e.target.value);
  }

  return (
    <div>
      <NavAdmin />
      <div className="container">
        <div className="form-group">
          <p>Writing a question</p>
          <input onChange={onChangeAQuestion} value={aQuestion} />
          <p>Writing a description of question</p>
          <input onChange={onChangeDescription} value={description} />
          <p>Writing wrong answers</p>
          <div className="vstack gap-3">
            {answers.map((text, index) => {
              return (
                <input
                  value={answers[index]}
                  key={index}
                  onChange={(e) => {
                    answers[index] = e.target.value;
                    setAnswers([...answers]);
                  }}
                />
              );
            })}
          </div>
          <button onClick={addAnswer}> + ADD ANSWER</button>
          <p>Writing a correct question</p>
          <input value={correct} onChange={onChangeCorrect} />

          <button onClick={saveQuestion}>SUBMIT</button>
        </div>
      </div>
    </div>
  );
}
