import React from "react";
import { useState } from "react";

function Question({
  aQuestion,
  answers,
  correct,
  computeScore,
  onAnswered,
  index,
  isFinished,
}) {
  const [message, setMessage] = useState();
  const [answered, setAnswered] = useState(false);
  const [selected, setSelected] = useState();

  function computeAnswer(answer, correctAns) {
    setSelected(answer);
    onAnswered();
    if (answer === correctAns) {
      setMessage("correct Answer");
      computeScore();
    } else {
      setMessage("Incorrect Answer");
    }
    setAnswered(true);
  }
  return (
    <div className="container mt-2 bg-white">
      {index + 1 + ". " + aQuestion}
      {answers.map((text, _id) => (
        <div className="mt-2" key={_id}>
          {console.log(text === correct)}
          <button
            disabled={answered}
            className={
              "btn btn-outline-primary w-100 text-start " +
              (text === selected && !isFinished ? "btn-secondary" : "") +
              (correct === selected &&
              answered &&
              selected === text &&
              isFinished
                ? "bg-success text-white"
                : "") +
              (selected !== correct &&
              text === selected &&
              answered &&
              isFinished
                ? "bg-danger text-white"
                : "")
            }
            onClick={() => {
              computeAnswer(text, correct);
            }}
          >
            {text}
          </button>
        </div>
      ))}
      {isFinished && <p>{message}</p>}
    </div>
  );
}
export default Question;
